import React from 'react';
import Hero from './Hero';
import HelpGallery from './HelpGallery';
import DeviceSelector from './DeviceSelector';

class Help extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            mobile:false,
            desktop:false
        }
    }

    toggleSlides = (component) => { // toggle slideshow for mobile or desktop
        if(this.state[component]){
            this.setState({[component]:false})
        }
        else{
            this.setState({[component]:true})
        }
    }

    render(){
        return(
            <div>
                <Hero image="/images/help.svg" heroHeader='Getting Stuck?' heroText='Follow this guide to get started'/>
                <h2>Which Device are you using?</h2>
                <DeviceSelector device = "mobile.jpeg" deviceName = "mobile" toggle ={this.toggleSlides}/>
                <DeviceSelector device = "desktop.jpeg" deviceName = "desktop" toggle ={this.toggleSlides}/>
                {(this.state.mobile)? <HelpGallery media="mobile"/>: null}
                {(this.state.desktop)? <HelpGallery media="desktop"/>: null}

            </div>
        )
    }
}

export default Help;
