import React from 'react';
import Hero from './Hero';
import HelpGallery from './HelpGallery';
import DeviceSelector from './DeviceSelector';
import './Help.scss';

class Help extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            mobile:false,
            desktop:false,
            btn:true
        }
    }

    toggleSlides = (component) => { // toggle slideshow for mobile or desktop
        if(this.state[component]){
            this.setState({[component]:false})
        }
        else{
            this.setState({[component]:true})
        }
    }

    render(){
        return(
            <div className="help">
                <Hero image="/images/help.svg" heroHeader='Getting Stuck?' heroText='Follow this guide to get started'/>
                <h2 className>Which Device are you using?</h2>
                <div className="help-options-container">
                {(this.state.btn)? <DeviceSelector device = "mobile" deviceBtn="btn" toggle ={this.toggleSlides}/>
                    : null}
                {(this.state.btn)? <DeviceSelector device = "desktop" deviceBtn="btn" toggle ={this.toggleSlides}/>
                    : null}
                {(this.state.mobile)? <HelpGallery media="mobile"/>
                    : null}
                {(this.state.desktop)? <HelpGallery media="desktop"/>
                    : null}
                </div>
            </div>
        )
    }
}

export default Help;
