import React from 'react';
import './HelpGallery.scss'

class HelpGallery extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            count:1,
            mobile: [
                {   id:0,
                    image:"mobile-image-1.jpg",
                    header: 'step 1',
                    desc:'this is where the description goes'      
                },
                {   id:1,
                    image:"mobile-image-2.jpg",
                    header: 'step 2',
                    desc:'this is where the description goes'      
                },
                {   id:2,
                    image:"mobile-image-3.jpg",
                    header: 'step 3',
                    desc:'this is where the description goes'      
                },
                {   id:3,
                    image:"mobile-image-4.jpg",
                    header: 'step 4',
                    desc:'this is where the description goes'      
                },
                {   id:4,
                    image:"mobile-image-5.jpg",
                    header: 'step 5',
                    desc:'this is where the description goes'      
                }
            ],
            desktop: [
                {   id:0,
                    image:"desktop-image-1.jpg",
                    header: 'step 1',
                    desc:'this is where the description goes'      
                },
                {   id:1,
                    image:"desktop-image-2.jpg",
                    header: 'step 2',
                    desc:'this is where the description goes'      
                },
                {   id:2,
                    image:"desktop-image-3.jpg",
                    header: 'step 3',
                    desc:'this is where the description goes'      
                },
                {   id:3,
                    image:"desktop-image-4.jpg",
                    header: 'step 4',
                    desc:'this is where the description goes'      
                },
                {   id:4,
                    image:"desktop-image-5.jpg",
                    header: 'step 5',
                    desc:'this is where the description goes'      
                }
            ]
        }
    }

    handleDecrement = () => {
        if(this.state.count > 0){
            this.setState({count:this.state.count -1})
        }
        else{
            return
        }
    };

    handleIncrement = () => {
        if(this.state.count <= this.state[this.props.media].length -1){
            this.setState({count:this.state.count + 1})
        }
        else{
            return
        }
    };

    render(){
        const count = this.state.count;
        const media = this.props.media;
        return(
            <div className="gallery-container">
                <img src={this.state[media][count].image} alt={media[count].image}></img>
                <h3>{this.state[media][count].header}</h3>
                <p>{this.state[media][count].desc}</p>
                <button onClick={this.handleDecrement}>prev</button>
                <button onClick={this.handleIncrement}>next</button>
            </div>
        )
    }

}
export default HelpGallery;
