import React from 'react';
import Hero from './Hero';
import HelpGallery from './HelpGallery';
import DeviceSelector from './DeviceSelector';
import './Help.scss';

class Help extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            mobile:false,
            desktop:false
        }
    }

    toggleSlides = (component) => { // toggle slideshow for mobile or desktop
        if(this.state[component]){
            this.setState({[component]:false})
        }
        else{
            this.setState({[component]:true})
        }
    }

    render(){
        return(
            <div className="help">
                <Hero image="/images/help.svg" heroHeader='Getting Stuck?' heroText='Follow this guide to get started'/>
                <h2 className>Which Device are you using?</h2>
                <div className="help-options-container">
                    <DeviceSelector device = "mobile" deviceName = "mobile" toggle ={this.toggleSlides}/>
                    <DeviceSelector device = "desktop" deviceName = "desktop" toggle ={this.toggleSlides}/>
                </div>
                {(this.state.mobile && this.state.desktop === false)? <HelpGallery media="mobile"/>
                : null}
                {(this.state.desktop && this.state.mobile === false)? <HelpGallery media="desktop"/>
                : null}

            </div>
        )
    }
}

export default Help;
